mapper.update:
UPDATE EMPLOYEE
SET ENAME=#{eName}, EID=#{eId}, EMAIL=#{email}, PHONE=#{phone}, ADDRESS=#{address}, JOIN_DATE=#{join_date}, #{eBirth}
WHERE EID=#{eId}

mapper.select:
SELECT COUNT(*)
FROM EMPLOYEE
WHERE EID=#{eId}

SELECT COUNT(*)
FROM EMPLOYEE
WHERE EMAIL=#{email}

mapper.update:
UPDATE ATTACHMENT
SET FILEPATH=#{filePath},
	ORIGINNAME=#{originName},
	CHANGENAME=#{changeName}
WHERE EID=#{eId}
